import React from 'react'
import ReactDOM from 'react-dom'
import TablaBuscar from './tablaBuscar'

class Buscar extends React.Component{
    buscar = async (e) => {
        e.preventDefault()
        console.log(this.props)
        let mayuscula = document.getElementById('palabra')
        let palabra  = await this.props.producto.filter(async (producto) => { const nombre = await producto.nombre
                                                                            const text = await document.getElementById('palabra').value
                                                                            console.log(nombre)
                                                                            console.log(text)
                                                                            return nombre.indexOf(text)
                                                                        })
        console.log(palabra)
        ReactDOM.render(
            <TablaBuscar aux={palabra}/>,document.getElementById('search')
        )
    }
    render(){
        console.log(this.props)
        return(
            <div>
                <form onSubmit={this.buscar}>
                    <input type='text' id='palabra' />
                    <input type='submit' value='Buscar'/>
                </form>
            </div>
        )
    }
}

export default Buscar
