import React  from 'react'

class Tabla extends React.Component{
    render(){
        console.log(this.props)
        return(
            <table>
                <thead>
                    <tr>
                        <th>Nombre</th>
                        <th>Codigo de Barras</th>
                        <th>Cantidad</th>
                        <th>Precio Unitario</th>
                        <th>Agregar Existencias</th>
                        <th>Comprar</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        this.props.aux.map((producto) => {
                            return(
                                        <tr key = {producto._id}>
                                            <td>{producto.nombre}</td>
                                            <td>{producto.codigo_barras}</td>
                                            <td>{producto.cantidad}</td>
                                            <td>{producto.precio}</td>
                                            <td><button onClick = {this.updateProducto} name = {producto._id}>Agregar</button></td>
                                            <td><button onClick = {this.restaProducto} name = {producto._id}>Comprar</button></td>
                                        </tr> 
                            )
                        })
                    }
                </tbody>
            </table>
        )
    }
}
export default Tabla
