import React from 'react'
import Tabla from './tabla'

class Buscar extends React.Component{
    buscar = async (e) => {
        e.preventDefault()
        console.log(this.props)
        let palabra  = await this.props.producto.filter(producto => producto.nombre == document.getElementById('palabra').value)
        console.log(palabra)
        ReactDOM.render(
            <Tabla aux={palabra}/>
        )
    }
    render(){
        console.log(this.props)
        return(
            <div>
                <form onSubmit={this.buscar}>
                    <input type='text' id='palabra' />
                    <input type='submit' value='Buscar'/>
                </form>
            </div>
        )
    }
}

export default Buscar
