import React from 'react'
import ProductoService from '../service/productoService'

class  Comprar extends React.Component{
    resta = async (e) => {
        e.preventDefault()
        let numero = parseInt(document.getElementById('res').value)
        let product = this.props.id
        product.cantidad = product.cantidad-numero
        let productoService = new ProductoService()
        let resp = await productoService.updateProducto(product)

        windows.location = '/home'
    }
    render(){
        return(
            <div>
                <form onSubmit = {this.resta}>
                    <input type='number' id='res' step='1' value='0' />
                    <input type='submit' value='Enviar'/>
                </form>
            </div>
        )
    }
}
export default Comprar
