import React from 'react'
import ReactDOM from 'react-dom'
import Agregar from './agregar'

class Producto extends React.Component{
    updateProducto = async (e) => {
        e.preventDefault()
        let aux = this.props.variable.producto.find(element => (element.id === parseInt(e.target.name)))
        console.log(aux)
        ReactDOM.render(
            <Agregar id={e.target.name}/>,document.getElementById('prods')
        )
    }
    render(){
        console.log(this.props.variable.productos)
        console.log(typeof(this.props.variable.productos))
        return(
            <table>
                <thead>
                    <tr>
                        <th>Nombre</th>
                        <th>Codigo de Barras</th>
                        <th>Cantidad</th>
                        <th>Precio Unitario</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        this.props.variable.productos.map((producto) => {
                            return(
                                <tr key = {producto._id}>
                                    <td>{producto.nombre}</td>
                                    <td>{producto.codigo_barras}</td>
                                    <td>{producto.cantidad}</td>
                                    <td>{producto.precio}</td>
                                    <td><button onClick = {this.updateProducto} name = {producto._id}>Modificar</button></td>
                                </tr>
                            )
                        })
                    }
                </tbody>
            </table>
        )
    }
}

export default Producto
