import React from 'react'
import ReactDOM from 'react-dom'
import Agregar from './agregar'
import AgregarProducto from './agregarProducto'
import Tabla from './tabla'

class Producto extends React.Component{
    agregarProducto = async (e) => {
        e.preventDefault()
        ReactDOM.render(
            <AgregarProducto/>,document.getElementById('prods')
        )
    }
    buscar = async (e) => {
        e.preventDefault()
        console.log(e.target.value)
        let search = this.props.variable.productos.filter(producto  => producto.nombre==e.target.value)
        console.log(search)
    }
    render(){
        let sucursalA = this.props.variable.productos.filter(sucursal => sucursal.sucursal=='A')
        let sucursalB = this.props.variable.productos.filter(sucursal => sucursal.sucursal=='B')

        return(
            <div>
                <Tabla aux={sucursalA}/>
                <Tabla aux={sucursalB}/>
                <button onClick={this.agregarProducto}>Agregar Producto</button>
                <input type='text' onChange={this.buscar}/>
            </div>
        )
    }
}

export default Producto
