import React from 'react'
import ProductoService from '../service/productoService'

class AgregarProducto extends React.Component{
    sucursales = ['A','B']
    guardar = async (e) => {
        e.preventDefault()
        let producto = []
        producto['nombre']=document.getElementById('nombre')
        producto['codigo']=document.getElementById('codigo')
        producto['cantidad']=document.getElementById('cantidad')
        producto['precio']=document.getElementById('precio')
    }
    render(){
        return(
            <div>
                <form onSubmit={this.guardar}>
                    <select>{
                        this.sucursales.map((sucursal) => {
                            <option value={sucursal}>{sucursal}</option>
                        })
                    }
                    </select>
                    <br/>
                    Nombre: <input type='text' id='nombre' required/><br/>
                    Codigo de Barras: <input type='text' id='codigo' required/><br/>
                    Cantidad: <input type='number' id='cantidad' required/><br/>
                    Precio: <input type='number' id='precio' required/><br/>
                    <input type='submit' value='Enviar'/>
                </form>
            </div>
        )
    }
}

export default AgregarProducto
