import React from 'react'
import ReactDOM from 'react-dom'
import Agregar from './agregar'
import Tabla from './tabla'
import Comprar from './comprar'

class Producto extends React.Component{
    updateProducto = async (e) => {
        e.preventDefault()
        let aux = this.props.variable.productos.find(element => (element._id === e.target.name))

        ReactDOM.render(
            <Agregar id={aux}/>,document.getElementById('prods')
        )
    }
    /*restaProducto = async (e) => {
        e.preventDefault()
        let aux = this.props.variable.productos.find(element => (element.id === e.target.name))
        ReactDOM.render(
            <Comprar id={aux}/>,document.getElementById('prods')
        )
    }*/
    render(){
        let sucursalA = this.props.variable.productos.filter(sucursal => sucursal.sucursal=='A')
        let sucursalB = this.props.variable.productos.filter(sucursal => sucursal.sucursal=='B')

        return(
            <div>
                <Tabla aux={sucursalA}/>
                <Tabla aux={sucursalB}/>
            </div>
        )
    }
}

export default Producto
